sys_script_include.script:
var getIncident = Class.create();
getIncident.prototype = {
    initialize: function(incident) {
        this.arr = [];
        this.grIncident = new GlideRecord("incident"); //Tabela de Incidents
        this.grIncident.addQuery("number", "INC0000036");
        this.grIncident.query();
        while (this.grIncident.next()) {
            this.obj = {}; //Objeto com os campos
            this.obj.assigned_to = this.grIncident.assigned_to.getDisplayValue();
            this.obj.priority = this.grIncident.priority.getDisplayValue();
            this.obj.short_description = this.grIncident.short_description.getDisplayValue();
            this.arr.push(this.obj);
            return this.arr; //Objeto GlideRecord
        }
    },

    type: 'getIncident'
};
