sys_script_include.script:
var getUserInfoAndre = Class.create();
getUserInfoAndre.prototype = {
    initialize: function(sys_id) {
        this.arr = [];
        this.grUser = new GlideRecord("sys_user"); //Tabela de usuários
        this.grUser.addQuery("sys_id", sys_id);
        this.grUser.query();
        while (this.grUser.next()) {
            this.obj = {}; //Objeto com os campos
            this.obj.first_name = this.grUser.first_name.getDisplayValue();
            this.obj.last_name = this.grUser.last_name.getDisplayValue();
            this.obj.email = this.grUser.email.getDisplayValue();
            this.obj.company = this.grUser.company.name.getDisplayValue();
            this.arr.push(this.obj);
            return this.arr; //Objeto GlideRecord
        }
    },

    type: 'TestBackgroundScriptUtils'
};
